mapper.select:
select c.ZFB_ID, c.user_name userName, c.application_id applicationId, c.identity_card identityCard, c.realtype verifyResult, c.addrScore, c.zmScore, decode(c.results,'1','失败','0','成功') results, decode(c.isInstinct,'1','已导入','0','未导入') isInstinct, decode(c.is_local,'1','调库','0','非调库') isLocal, get_split_reason(c.calltype,'ZMXY_QUERY_TYPE','-') callType , to_char(c.call_date,'yyyy-mm-dd') call_date, to_char(c.record_date,'yyyy-mm-dd') record_date from ( select rownum r, a.*from ( select * from pf_zfb_record pf where 1=1
AND TO_char(pf.call_date,'yyyy-mm-dd') >= #{beginTime}
AND #{endTime} >= TO_char(pf.call_date,'yyyy-mm-dd')
AND TO_char(pf.record_date,'yyyy-mm-dd') >= #{beginTime2}
AND #{endTime2} >= TO_char(pf.record_date,'yyyy-mm-dd')
AND pf.application_id like '%'||#{applicationId}||'%'
AND pf.identity_card like '%'||#{identityCard}||'%'
AND pf.results = #{results}
AND pf.isInstinct = #{isInstinct}
AND pf.callType = #{callType}
AND pf.is_Local = #{isLocal}
order by to_number(zfb_ID) desc ) a) c WHERE c.r >#{startRow} AND rownum <= #{pageSize} order by to_number(ZFB_ID) desc


	select count(1)
	from (
		select *
		from pf_zfb_record pf
		where 1=1
		
		
			AND TO_char(pf.call_date,'yyyy-mm-dd') >=
			#{beginTime}
		
		
			AND #{endTime} >= TO_char(pf.call_date,'yyyy-mm-dd')
		
		
			AND TO_char(pf.record_date,'yyyy-mm-dd') >=
			#{beginTime2}
		
		
			AND #{endTime2} >= TO_char(pf.record_date,'yyyy-mm-dd')
		
		
		
			AND pf.application_id like '%'||#{applicationId}||'%'
		
		
			AND pf.identity_card like '%'||#{identityCard}||'%'
		
		
			AND pf.results = #{results}
		
		
			AND pf.isInstinct = #{isInstinct}
		
		
			AND pf.callType = #{callType}
		
		
			AND pf.is_Local = #{isLocal}
		
	)temp
